import React from "react";
import "./channeldescription.css";
import { togglePanel } from "../../../api/logic/toggle";
import toEmoji from "../../../api/logic/toEmoji";
import { AiOutlineDown, AiOutlinePlus } from "react-icons/ai";

export default function ChannelDescription( {selectedChannel} ) {
        return(
        <div className="channel-info">

                <div className="channel-display">
                    <div className="icon-wrapper">
                        <h1>{toEmoji(selectedChannel.icon)}</h1>
                    </div>
                    <div className="channel-title">
                        <h1>{selectedChannel.name}</h1>
                    </div> 
                </div>

                <div className="private-chats">
                    <header>
                        <div className="private-toggle" onClick={togglePanel(".members-wrapper")}>
                            <AiOutlineDown/>
                        </div>
                        <div className="title-wrapper">
                            <h4>Members</h4>
                            <p className="private-channel-count">({selectedChannel.members.length})</p>
                        </div>
                        <div className="private-add-channel">
                            <AiOutlinePlus/>
                        </div>
                    </header>
                </div>
                <div className="members-list">
                        {selectedChannel.members.map(member => {
                            return <div key={member} className="member">{member}</div>
                        })}
                </div>
        </div> 


        )
}
