import React from "react";
import "./channeldescription.css";
import { toggleMembersPanel, togglePanel } from "../../../api/logic/toggle";
import { AiOutlineDown } from "react-icons/ai";

export default function ChannelDescription( {selectedChannel} ) {
        return(
        <div className="channel-info">
            <header>
                <div className="channel-display">
                    <div className="icon-wrapper">
                        <h1>&#128640;</h1>
                    </div>
                    <div className="channel-title">
                        <h1>{selectedChannel.name}</h1>
                    </div> 
                </div>
                <div className="members">
                    <header>
                        <h1 className="members-section-title">Members</h1>
                        <h1 className="members-count">{selectedChannel.members.length}</h1>
                        <div className="toggle" onClick={togglePanel(".members-list")}>
                            <AiOutlineDown/>
                        </div>
                    </header>
                    <div className="members-list">
                        {selectedChannel.members.map(member => {
                            return <div key={member} className="member">{member}</div>
                        })}
                    </div>
                </div>

            </header>
        </div>
        )
}
