export function togglePanel(className) {
    const panel = document.querySelector(className);
    const currentMode = panel.style.display;
    panel.style.display = currentMode === "none" ? "inline" : "none";
}


export function toggleChannelPanel() {
    const panel = document.querySelector(".channels-wrapper");
    const currentMode = panel.style.display;
    panel.style.display = currentMode === "none" ? "inline" : "none";
}

export function toggleMembersPanel() {
    const panel = document.querySelector(".members-list");
    const currentMode = panel.style.display;
    panel.style.display = currentMode === "none" ? "inline" : "none";
}
