import { React, useState, useEffect } from "react";
import "./app.css";

import { getUsers } from "./api/logic/getters";

import ChatPage from "./components/page/ChatPage";

import { HubConnectionBuilder, LogLevel } from '@microsoft/signalr';

const axios = require('axios');

const URL = "https://7def-185-129-94-115.eu.ngrok.io"

function App() {

    const [currentUser, setCurrentUser] = useState({});
    // const currentUser = getUsers()[0]; 

    const loginHandler = async () => {
        const id = parseInt(document.querySelector("input").value);
        // const user = await connection.invoke("GetUser", id);
        // console.log("current user:", user);
        // setCurrentUser(currentUser => user);
        // setCurrentUser(currentUser => getUsers()[id-1]);
        axios.get(`${URL}/getuser/${id}`).then(resp => {
            setCurrentUser(currentUser => resp.data);
        });
    }

    return(
        <div className="page">
            {Object.keys(currentUser).length !== 0 ?
            <ChatPage 
            currentUser={currentUser}/>
            :
            <div className="login">
                <input/>
                <button onClick={loginHandler}>Login</button>
            </div>}
        </div>
    )
}

export default App;
