import { React, useState } from "react";
import "./app.css";

import { getChannels, getUsers, getUserChannel } from "./api/logic/getters";

import Sidebar from "./components/sidebar/Sidebar";
import ChatLobby from './components/lobby/ChatLobby';

import { HubConnectionBuilder, LogLevel } from '@microsoft/signalr';


function App() {

    const currentUser = getUsers()[0];

    const [channels, setChannels] = useState(getUserChannel(currentUser.id, getChannels()));
    const [users, setUsers] = useState(getUsers());
    const [selectedChannel, setSelectedChannel] = useState({});    

    async function connect(){
        try {
            const connection = new HubConnectionBuilder()
            .withUrl("https://57d5-185-129-94-115.eu.ngrok.io/chatHub")
            .configureLogging(LogLevel.Information)
            .build();

            console.log("SignalR Connected.");

            connection.on("AddChannel", (channel) => console.log(channel));

            connection.onclose(e => {
                setConnection();
            });

            await connection.start();
            setConnection(connection);
        } catch (err) {
            console.log(err);
        }
    }

    const createChannel = async (e, channel, user) => {
        try {
            e.preventDefault();
            const roomName = document.querySelector('input').value;
            await connection.invoke("CreateRoom", roomName, user.name);
        } catch (err) {
            console.log(err);
        }
    }

    const sendMessage = async (message) => {
        try {
          await connection.invoke("SendMessage", message);
        } catch (err) {
          console.log(err);
        }
      }    

    return(
        <div className="page">
            <Sidebar/>
            <ChatLobby 
            currentUser={currentUser}
            channels={channels}
            setChannels={setChannels}
            users={users}
            setUsers={setUsers}
            selectedChannel={selectedChannel}
            setSelectedChannel={setSelectedChannel}
            connect={connect} 
            createChannel={createChannel} 
            sendMessage={sendMessage}/>
        </div>
    )
}

export default App;
