import { URL } from "../serverUrl";

const axios = require("axios");

export const getUser = (userId, onRequest=()=>{}, onError=()=>{}) => {
    axios.get(`${URL}/getuser/${userId}`).then(resp => {
        onRequest(resp.data);
    }).catch(err => onError(err));
}
