import { URL } from "../serverUrl";

const axios = require("axios");

export const getUser = async (userId, onRequest=()=>{}, onError=()=>{}) => {
    axios.get(`${URL}/getuser/${userId}`).then(resp => {
        onRequest(resp.data);
    }).catch(err => onError(err));
}

export const getAllUsers = (onRequest=()=>{}, onError=()=>{}) => {
    axios.get(`${URL}/user`).then(resp => {
        onRequest(resp.data);
    }).catch(err => onError(err));
}
