import React from "react";
import './teamchat.css';
import Channel from "../channel/Channel";
import { AiOutlineDown, AiOutlinePlus } from "react-icons/ai";
import { toggleChannelPanel, togglePanel } from "../../api/logic/toggle";
import PrivateChat from "../channel/PrivateChat";
import { users } from "../../api/data/users";


export default function TeamChat( { channels, setChannels, selectedChannel, setSelectedChannel, currentUser } ) {

    return (
        <div className="team-chat">
                <div className="chat-intro">
                    <h1>Team chat</h1>
                </div>
                <div className="private-chats">
                    <header>
                        <div className="private-toggle" onClick={togglePanel(".channels-wrapper")}>
                            <AiOutlineDown/>
                        </div>
                        <h4>Public Channels</h4>
                        <p className="private-channel-count">({channels.length})</p>
                        <div className="private-add-channel">
                            <AiOutlinePlus/>
                        </div>
                    </header>
                </div>
                {/* <div className="chat-header">
                    <div className="header-wrapper">
                        <div className="channel-toggle">
                            <div className="toggle" onClick={togglePanel(".channels-wrapper")}>
                                <AiOutlineDown/>
                            </div>
                            <h4>Public Channels</h4>
                            <div className="channel-count"><p>({channels.length})</p></div>
                            <div className="add-channel">
                                <AiOutlinePlus/>
                            </div>
                        </div>
                     </div>
                </div> */}
            <div className="channels-wrapper">
                <div className="channels">
                    {channels.map((channel) => {
                        return <Channel key={channel.id}
                        channel={channel}
                        selectedChannel={selectedChannel}
                        selected={channel.id === selectedChannel.id}
                        setSelectedChannel={setSelectedChannel}
                        />;
                    })}
                </div>
            </div>
            {<PrivateChat users={users}/>}

        </div>
    )

}
