import React from "react";

import "./privatechat.css";

import UserIcon from "../userIcon/UserIcon";
import PanelHeader from "../panelheader/PanelHeader";
import { getUserInfo } from "../../api/logic/getters";


export default function PrivateChat({ currentUser, channels, selectedChannel, setSelectedChannel }){

    const privateChannels = channels.filter(channel => channel.name === "");

    return(
        <div className="private-channel-panel">
            <PanelHeader
            key={Math.random()*10000} 
            title={"Private Channels"} 
            count={privateChannels.length} 
            panelWrapperClass={".private-channels"} 
            onAddClick={null}/>

            {privateChannels.length !== 0 ? 
                <div className="private-channels">
                {
                    privateChannels.map(channel => {
                        const otherUserId = channel.members.filter(member => member.id !== currentUser.id)[0];
                        const otherUser = getUserInfo(otherUserId);
                        return(
                            <div className="private">
                                <div className="channel-selected" data-selected={(channel.id === selectedChannel.id).toString()}>
                                    <UserIcon key={otherUser.id} userName={otherUser.name}/>
                                    <p className="member-name">{otherUser.name}</p>
                                    <p className="private-message-count">({channel.messages.length})</p>
                                </div>
                            </div>
                        )
                    })
                }
                </div> 
            : ""}


        </div>
    );
};
