import React from "react";
import { BrowserRouter } from "react-router-dom";
import "./app.css";

import Sidebar from "./components/sidebar/Sidebar";
import ChatLobby from './components/lobby/ChatLobby';
import { users } from "./api/data/users";

import { HubConnectionBuilder, LogLevel } from '@microsoft/signalr';

import { useState } from "react";

function App() {

    const [connection, setConnection] = useState();

    async function start() {
        try {
            const connection = new HubConnectionBuilder()
            .withUrl("https://57d5-185-129-94-115.eu.ngrok.io/chatHub")
            .configureLogging(LogLevel.Information)
            .build();

            await connection.start();
            console.log("SignalR Connected.");

            connection.on("AddChannel", (roomVm) => console.log(roomVm));

            connection.onclose(async () => {
                await start();
            })

            setConnection(connection);

        } catch (err) {
            console.log(err);
            setTimeout(start, 5000);
        }
    };

    return(
        <div className="page">
            <Sidebar/>
            <ChatLobby start={start} connection={connection}/>
        </div>
        // <BrowserRouter>
        //     <div className="page">
        //         <Sidebar/>
        //         <ChatLobby/>
        //     </div>
        // </BrowserRouter>
    )
}

export default App;
