import {React, useState, useEffect} from "react";

import { HubConnectionBuilder, LogLevel } from '@microsoft/signalr';

import Sidebar from "../sidebar/Sidebar";
import ChatLobby from '../lobby/ChatLobby';

const URL = "https://7def-185-129-94-115.eu.ngrok.io"

export default function ChatPage( 
    {currentUser} ){

    console.log("CURRENT USER ROOMS:", currentUser.roomExistUsers);
    const [channels, setChannels] = useState(currentUser.roomExistUsers);
    // const [users, setUsers] = useState(getUsers());
    const [selectedChannel, setSelectedChannel] = useState({});   

    const [connection, setConnection] = useState();

    console.log("Current user: ", currentUser);

    useEffect(() => {
        // declare the data fetching function
        const connect = async () => {
            try {
                const connection = new HubConnectionBuilder()
                .withUrl(`${URL}/chatHub`)
                .configureLogging(LogLevel.Information)
                .build();

                console.log("SignalR Connected.");

                connection.on("AddChannel", (channel) => console.log(channel));

                connection.on("onError", (msg) => console.log(msg));

                connection.on("RecieveMessage", (message) => {
                    console.log("RECIEVING MESSAGE: ", message, "\nCURRENT USER:", currentUser);
                    const copyChannel = Object.assign({}, currentUser.roomExistUsers.filter(room => room.id === message.toRoomId)[0]);
                    if (copyChannel.messages !== null){
                        copyChannel.messages.push(message);
                    } else {
                        copyChannel.messages = [message];
                    }

                    setChannels(channels => {
                        channels.map(channel => {
                            if (channel.id !== copyChannel.id){
                                return channel;
                            } else {
                                return copyChannel;
                            }
                        })
                    });
                });

                connection.onclose(e => {
                    setConnection();
                });

                await connection.start();
                setConnection(connection);
            } catch (err) {
                console.log(err);
            };
        };

        // call the function
        connect()
          // make sure to catch any error
          .catch(console.error);
      }, [])

    const createChannel = async (channel, user, members) => {
        try {
            await connection.invoke("CreateRoom", channel, user, members);
        } catch (err) {
            console.log(err);
        }
    }

    const sendMessage = async (channel, user, message) => {
        try {
          await connection.invoke("SendToRoom", channel, user, message);
        } catch (err) {
          console.log(err);
        }
    }    

    const getAllUsers = async () => {
        try {
            const data = await connection.invoke("GetAllUsers");
            return data;
        } catch (err) {
            console.log(err);
        }
    }

    return(
        <div className="page">
            <Sidebar/>
            <ChatLobby 
            currentUser={currentUser}
            channels={channels}
            setChannels={setChannels}
            // users={users}
            // setUsers={setUsers}
            selectedChannel={selectedChannel}
            setSelectedChannel={setSelectedChannel}
            createChannel={createChannel} 
            sendMessage={sendMessage}
            getAllUsers={getAllUsers}/>
        </div>
    );
};
